import React, { useState } from 'react';
import axios from 'axios';
import './styl'

function App() {
  const [questionPaper, setQuestionPaper] = useState(null);
  const [answerSheet, setAnswerSheet] = useState(null);
  const [downloadLink, setDownloadLink] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false); // State for loader
  const [showNotification, setShowNotification] = useState(false); // State for notification

  const handleFileChange = (e, setFile) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!questionPaper || !answerSheet) {
      setError('Please upload both question paper and answer sheet.');
      return;
    }

    setIsLoading(true); // Show loader
    setError('');
    setShowNotification(false); // Hide notification

    const formData = new FormData();
    formData.append('questionPaper', questionPaper);
    formData.append('answerSheet', answerSheet);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        responseType: 'blob', // Ensure the response is treated as a binary file
      });

      // Create a URL for the blob
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'final_output.xlsx'); // Set the file name
      document.body.appendChild(link);
      link.click(); // Trigger the download
      link.remove(); // Clean up

      setDownloadLink(url);
      setShowNotification(true); // Show notification
    } catch (err) {
      setError('Failed to process files. Please try again.');
      console.error(err);
    } finally {
      setIsLoading(false); // Hide loader
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Question Paper Processor</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="questionPaper" className="form-label">Upload Question Paper:</label>
          <input
            type="file"
            className="form-control"
            id="questionPaper"
            onChange={(e) => handleFileChange(e, setQuestionPaper)}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="answerSheet" className="form-label">Upload Answer Sheet:</label>
          <input
            type="file"
            className="form-control"
            id="answerSheet"
            onChange={(e) => handleFileChange(e, setAnswerSheet)}
          />
        </div>
        <button type="submit" className="btn btn-primary" disabled={isLoading}>
          {isLoading ? (
            <>
              <Spinner
                as="span"
                animation="border"
                size="sm"
                role="status"
                aria-hidden="true"
              />
              <span className="ms-2">Processing...</span>
            </>
          ) : (
            'Process Files'
          )}
        </button>
      </form>

      {error && <div className="alert alert-danger mt-3">{error}</div>}

      {showNotification && (
        <Alert variant="success" className="mt-3">
          File downloaded successfully!
        </Alert>
      )}
    </div>
  );
}

export default App;
